import React, { useEffect, useState, useRef } from 'react';
import { getRoute } from './tomtomService';
import L from 'leaflet';

function App() {
    const [start, setStart] = useState('51.505,-0.09');
    const [end, setEnd] = useState('51.51,-0.1');
    const [map, setMap] = useState(null);
    const mapInitializedRef = useRef(false); // Ref to track if the map has been initialized

    useEffect(() => {
        // Initialize the map
        const initMap = () => {
            const mapInstance = L.map('map').setView([51.505, -0.09], 13);
            L.tileLayer('https://api.tomtom.com/map/1/tile/basic/main/{z}/{x}/{y}.png?key=S4DA9p3Bk7NmcRIjkdZ1Q4APGpgGdBXa', {
                attribution: '&copy; TomTom'
            }).addTo(mapInstance);
            setMap(mapInstance);
            mapInitializedRef.current = true; // Mark the map as initialized
        };

        // Check if the map has already been initialized
        if (!mapInitializedRef.current) {
            initMap();
        }

        // Cleanup function to remove the map instance when the component unmounts
        return () => {
            if (map) {
                map.remove(); // Remove the map instance
                setMap(null); // Reset the map state
            }
        };
    }, [map]); // Dependency array containing map state

    const handleRouteFetch = async () => {
        const routeData = await getRoute(start, end);
        if (map) {
            // Clear existing layers if necessary
            map.eachLayer((layer) => {
                if (layer instanceof L.Polyline) {
                    map.removeLayer(layer);
                }
            });

            // Add the new route to the map
            const latLngs = routeData.legs[0].points.map(point => [point.latitude, point.longitude]);
            const polyline = L.polyline(latLngs, { color: 'blue' }).addTo(map);
            map.fitBounds(polyline.getBounds()); // Adjust map view to show the route
        }
    };

    return (
        <div className="App">
            <h1>GPS Routing with TomTom API</h1>
            <input
                type="text"
                value={start}
                onChange={(e) => setStart(e.target.value)}
                placeholder="Start (lat,long)"
            />
            <input
                type="text"
                value={end}
                onChange={(e) => setEnd(e.target.value)}
                placeholder="End (lat,long)"
            />
            <button onClick={handleRouteFetch}>Get Route</button>
            <div id="map" style={{ width: '100%', height: '600px' }}></div>
        </div>
    );
}

export default App;
