import React, { useEffect, useState, useRef } from 'react';
import { getRoute } from './tomtomService';
import L from 'leaflet';
import 'leaflet/dist/leaflet.css';
import './App.css';

function App() {
    const [start, setStart] = useState('51.505,-0.09');
    const [end, setEnd] = useState('51.51,-0.1');
    const [map, setMap] = useState(null);
    const mapInitializedRef = useRef(false);
    const mapContainerRef = useRef(null);

    useEffect(() => {
        const initMap = () => {
            const mapInstance = L.map(mapContainerRef.current).setView([51.505, -0.09], 13);
            L.tileLayer('https://api.tomtom.com/map/1/tile/basic/main/{z}/{x}/{y}.png?key=V6jo7iP3AoV0ql49vAAAQ6KDRDVcVAfD', {
                attribution: '&copy; TomTom'
            }).addTo(mapInstance);
            setMap(mapInstance);
            mapInitializedRef.current = true;
        };

        if (!mapInitializedRef.current) {
            initMap();
        }

        return () => {
            if (map) {
                map.remove();
                setMap(null);
            }
        };
    }, [map]);

    const fetchTrafficData = async (routeData) => {
        // Placeholder function to simulate fetching real-time traffic data
        // Here, you can use routeData to tailor your traffic fetching logic
        return [
            { segment: 'A', congestionLevel: 'low' },
            { segment: 'B', congestionLevel: 'high' },
            { segment: 'C', congestionLevel: 'medium' },
        ];
    };

    const predictTraffic = async (routeData) => {
        const trafficData = await fetchTrafficData(routeData); // Use routeData
        let predictedTime = calculateBaseTime(routeData); // Use routeData

        trafficData.forEach(segment => {
            if (segment.congestionLevel === 'high') {
                predictedTime *= 1.5; // Increase time by 50%
            } else if (segment.congestionLevel === 'medium') {
                predictedTime *= 1.2; // Increase time by 20%
            }
        });

        return predictedTime; // Return the predicted time for the route
    };

    const calculateBaseTime = (routeData) => {
        // Calculate the base time for the route based on distance or other factors
        // Assuming base time is calculated based on the number of segments in routeData
        const baseTimePerSegment = 5; // Assume 5 minutes per segment
        const numberOfSegments = routeData.legs[0].points.length - 1; // Calculate segments
        return baseTimePerSegment * numberOfSegments; // Total base time
    };

    const handleRouteFetch = async () => {
        try {
            const routeData = await getRoute(start, end);

            // Predict traffic and adjust the route accordingly
            const predictedTime = await predictTraffic(routeData); // Use routeData
            console.log('Predicted time with traffic:', predictedTime);

            if (map) {
                // Clear existing layers if necessary
                map.eachLayer((layer) => {
                    if (layer instanceof L.Polyline) {
                        map.removeLayer(layer);
                    }
                });

                // Add the new route to the map
                const latLngs = routeData.legs[0].points.map(point => [point.latitude, point.longitude]);
                const polyline = L.polyline(latLngs, { color: 'blue' }).addTo(map);
                map.fitBounds(polyline.getBounds()); // Adjust map view to show the route
            }
        } catch (error) {
            console.error('Failed to fetch route:', error);
            alert('Failed to fetch route. Please check your coordinates and try again.');
        }
    };

    return (
        <div className="App">
            <h1>GPS Routing with TomTom API</h1>
            <div className="input-container">
                <input
                    type="text"
                    value={start}
                    onChange={(e) => setStart(e.target.value)}
                    placeholder="Start (lat,long)"
                />
                <input
                    type="text"
                    value={end}
                    onChange={(e) => setEnd(e.target.value)}
                    placeholder="End (lat,long)"
                />
                <button onClick={handleRouteFetch}>Get Route</button>
            </div>
            <div ref={mapContainerRef} id="map" className="map-container" style={{ width: '100%', height: '600px', position: 'relative' }}></div>
        </div>
    );
}

export default App;
